using UnityEngine; using System.Collections;  // basic example of activation of a "trap"  public class PlateformFall : MonoBehaviour {      public float m_FallDelay = 1.0f;      private Rigidbody2D m_RigidBody; 	     void Awake() 	{         m_RigidBody = GetComponent<Rigidbody2D>(); 	} 	     void OnCollisionEnter2D (Collision2D other) 	{ 		if (other.gameObject.CompareTag("Player")) 		{             Invoke ("Fall", m_FallDelay); // call function Fall in m_FallDelay seconds 		} 	} 	     void Fall() 	{         m_RigidBody.isKinematic = false; // the object is now submitted to forces, he will now be affected by gravity  	} }  